package com.example.capstone.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.paging.PagingDataAdapterimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.capstone.databinding.ItemEventBindingimport com.example.capstone.model.Greeventsclass GreeventsAdapter :    PagingDataAdapter<Greevents, GreeventsAdapter.GreeventsViewHolder>(DIFF_CALLBACK) {    class GreeventsViewHolder(val binding: ItemEventBinding) : RecyclerView.ViewHolder(binding.root)    override fun onBindViewHolder(holder: GreeventsAdapter.GreeventsViewHolder, position: Int) {        val listEvents = getItem(position)        holder.binding.apply {            tvNameEvent.text = listEvents?.name            tvDate.text = listEvents?.date            tvDesc.text = listEvents?.deskripsi            Glide.with(holder.itemView.context)                .load(listEvents?.image_post)                .fitCenter()                .into(ivEventList)        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): GreeventsAdapter.GreeventsViewHolder {        return GreeventsViewHolder(            ItemEventBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    companion object {        val DIFF_CALLBACK = object : DiffUtil.ItemCallback<Greevents>() {            override fun areItemsTheSame(oldItem: Greevents, newItem: Greevents): Boolean {                return oldItem == newItem            }            override fun areContentsTheSame(oldItem: Greevents, newItem: Greevents): Boolean {                return oldItem.id == newItem.id &&                        oldItem.name == newItem.name &&                        oldItem.date == newItem.date            }        }    }}