package com.example.capstone.apiimport com.example.capstone.model.ResponseGreeventsimport com.example.capstone.model.ResponseLoginimport com.example.capstone.model.ResponseRegisterimport retrofit2.Responseimport retrofit2.http.*interface ApiService {    @FormUrlEncoded    @POST("apis/auth/login")    suspend fun login(        @Field("email") email: String,        @Field("password") password: String    ): ResponseLogin    @FormUrlEncoded    @POST("apis/auth/register")    suspend fun register(        @Field("name") name: String,        @Field("email") email: String,        @Field("password") password: String    ): ResponseRegister    @GET("apis/event")    suspend fun getAllEvents(        @Header("Authorization") token: String,    ) : Response<ResponseGreevents>}