package com.example.capstone.ui.homeimport androidx.lifecycle.LiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport androidx.paging.PagingDataimport androidx.paging.cachedInimport com.example.capstone.data.AppRepositoryimport com.example.capstone.model.Greeventsclass HomeViewModel(appRepository: AppRepository): ViewModel() {    val getAllEvents: LiveData<PagingData<Greevents>> =        appRepository.getAllEvents().cachedIn(viewModelScope)}